    // A mock message object that we will configure differently for each test case.
    private InterceptedTextMessage mockMessage;

    @BeforeEach
    void setUp() {
        // Create fresh mocks before each test to ensure tests are isolated.
        mockLogger = mock(Logging.class);
        mockMessage = mock(InterceptedTextMessage.class);
    }

    @Test
    void testHappyPath_FindsAndLogsAnswerExplanation() {
        // ARRANGE: Create a perfect JSON payload and configure the mock message.

        String perfectJson = testjson;
        // Configure the mock to behave like a real message with our perfect JSON.
        when(mockMessage.payload()).thenReturn(perfectJson);
        when(mockMessage.direction()).thenReturn(Direction.SERVER_TO_CLIENT);

        // Create a REAL instance of the class we are testing.
        InspectWebSocketMessage inspector = new InspectWebSocketMessage(mockLogger);

        // ACT: Call the method we want to test.
        inspector.getInspectedMessage(mockMessage);

        // ASSERT: Verify that the logger was called with the expected output.
        // We check that logToOutput was called with the specific explanation text.
        verify(mockLogger, times(1)).logToOutput("\"This is the correct answer!\"");
        // Also verify no errors were logged.
        //verify(mockLogger, never()).logToError(anyString());
    }

    @Test
    void testMissingNode_LogsErrorWhenOngoingQuestionIsNotFound() {
        // ARRANGE: Create JSON that is valid but missing the "ongoingQuestion" node.
        String jsonWithMissingNode = testjson;

        when(mockMessage.payload()).thenReturn(jsonWithMissingNode);
        when(mockMessage.direction()).thenReturn(Direction.SERVER_TO_CLIENT);

        InspectWebSocketMessage inspector = new InspectWebSocketMessage(mockLogger);

        // ACT
        inspector.getInspectedMessage(mockMessage);

        // ASSERT: Verify that the class correctly identified the missing node and logged an error.
        verify(mockLogger, times(1)).logToError("No ongoing question found");
        // Verify no successful output was logged.
        verify(mockLogger, never()).logToOutput(anyString());
    }

    @Test
    void testWrongDirection_IgnoresClientToServerMessage() {
        // ARRANGE: Configure the message to be going in the wrong direction.
        String anyJson = testjson;
        when(mockMessage.payload()).thenReturn(anyJson);
        when(mockMessage.direction()).thenReturn(Direction.CLIENT_TO_SERVER); // Wrong direction!

        InspectWebSocketMessage inspector = new InspectWebSocketMessage(mockLogger);

        // ACT
        inspector.getInspectedMessage(mockMessage);

        // ASSERT: Verify that NO logging happened at all, because the 'if' condition
        // for direction should have prevented any logic from running.
        verify(mockLogger, never()).logToOutput(anyString());
        verify(mockLogger, never()).logToError(anyString());
    }

    @Test
    void testMalformedJson_HandlesErrorGracefully() {
        // ARRANGE: Provide a broken, un-parsable JSON string.
        String malformedJson = testjson;

        when(mockMessage.payload()).thenReturn(malformedJson);
        when(mockMessage.direction()).thenReturn(Direction.SERVER_TO_CLIENT);

        InspectWebSocketMessage inspector = new InspectWebSocketMessage(mockLogger);

        // ACT
        inspector.getInspectedMessage(mockMessage);

        // ASSERT: Verify that the code didn't crash and that no successful output was logged.
        // The ObjectMapper will throw an exception internally, which is caught by the try-catch block.
        // We expect nothing to be logged in this case.
        verify(mockLogger, never()).logToOutput(anyString());
        verify(mockLogger, never()).logToError(anyString());
    }
